// This file contains an example to demonstrate how to use Foxit PDF SDK to combine form data and export these data to a CSV file.


// Include Foxit SDK header files.
#include <time.h>
#include<direct.h>
#include <stdio.h>

#include "../../../include/fs_basictypes_c.h"
#include "../../../include/fs_common_c.h"
#include "../../../include/fs_pdfdoc_c.h"
#include "../../../include/fs_pdfpage_c.h"
#include "../../../include/fs_pdfform_c.h"
#include "../../../include/fs_formcombination_c.h"

static const char* sn = "NfAuhaR9g2Ta9dFzHjK7gfP0ZVh78p5ds0GYxMy8DY0BdtD0zcIi8g==";
static const char* key = "";

static const wchar_t* output_path = L"../output_files/";
static const wchar_t* input_path = L"../input_files/";

#ifndef TRUE
#define TRUE 1
#endif

#ifndef FALSE
#define FALSE 0
#endif

#ifndef NULL
#define NULL (void*)0
#endif

#define MAX_FILE_PATH 260

int main(int argc, char *argv[]) {
  int err_ret = 0;
  wchar_t output_directory[MAX_FILE_PATH] = { 0 };
  FSErrorCode error_code = e_FSErrSuccess;
  error_code = FSDK_Library_Initialize(sn, key);
  if (error_code != e_FSErrSuccess) {
    printf("Library Initialize Error: %d\n", error_code);
    return 1;
  }

  swprintf_s(output_directory, MAX_FILE_PATH, L"%lsform_combination/", output_path);
  _wmkdir(output_directory);

  {
    FS_FORMFILEINFO_HANDLE  fileinfoarray[3];
    wchar_t pdf_inputfile[MAX_FILE_PATH] = { 0 };
    wchar_t fdf_inputfile[MAX_FILE_PATH] = { 0 };
    wchar_t xml_inputfile[MAX_FILE_PATH] = { 0 };
    FS_WSTR wstr;
    wchar_t output_file[MAX_FILE_PATH] = { 0 };
    int i = 0;

    printf("===Start combine form===\n");
    //Start to combine form in current PDF document.

    swprintf_s(pdf_inputfile, MAX_FILE_PATH, L"%ls7FormField.pdf", input_path);
    printf("Add 7FormField.pdf\n");

    swprintf_s(fdf_inputfile, MAX_FILE_PATH, L"%lsFoxitForm_data.fdf", input_path);
    printf("Add FoxitForm_data.fdf\n");

    swprintf_s(xml_inputfile, MAX_FILE_PATH, L"%lsformfield.xml", input_path);
    printf("Add formfield.xml\n");

    wstr.str = L"";
    wstr.len = 0;
    FSDK_FormFileInfo_Create(pdf_inputfile, &wstr, &fileinfoarray[0]);
    FSDK_FormFileInfo_Create(fdf_inputfile, &wstr, &fileinfoarray[1]);
    FSDK_FormFileInfo_Create(xml_inputfile, &wstr, &fileinfoarray[2]);

    swprintf_s(output_file, MAX_FILE_PATH, L"%lsFormfilecombination.csv", output_directory);
    FSDK_FormCombination_CombineFormsToCSV(fileinfoarray, 3, output_file, FALSE);
    
    for (i = 0; i < sizeof(fileinfoarray) / sizeof(fileinfoarray[0]); i++)
      FSDK_FormFileInfo_Release(fileinfoarray[i]);
  }

  FSDK_Library_Release();
  printf("===Form combination end===\n");
  return err_ret;
}
