// This file contains an example to demonstrate how to use Foxit PDF SDK to Convert a PDF file to a XML format file.

// Include Foxit SDK header files.

#include<direct.h>
#include <stdio.h>

#include "../../../include/fs_basictypes_c.h"
#include "../../../include/fs_common_c.h"
#include "../../../include/fs_convert_c.h"

#ifndef TRUE
#define TRUE 1
#endif

#ifndef FALSE
#define FALSE 0
#endif

#ifndef NULL
#define NULL (void*)0
#endif

#define MAX_FILE_PATH 260

static const char* sn = "NfAuhaR9g2Ta9dFzHjK7gfP0ZVh78p5ds0GYxMy8DY0BdtD0zcIi8g==";
static const char* key = "";

static const wchar_t* output_path = L"../output_files/";
static const wchar_t* input_path = L"../input_files/";

int main(int argc, char *argv[]) {
  int err_ret = 0;
  FSErrorCode error_code = e_FSErrSuccess;
  wchar_t output_directory[MAX_FILE_PATH] = { 0 };
  wchar_t src_pdf_path[MAX_FILE_PATH] = { 0 };
  wchar_t saved_xml_path[MAX_FILE_PATH] = { 0 };
  FS_BOOL ret;

  error_code = FSDK_Library_Initialize(sn, key);
  if (error_code != e_FSErrSuccess) {
    printf("Library Initialize Error: %d\n", error_code);
    return 1;
  }

  swprintf_s(output_directory, MAX_FILE_PATH, L"%lspdf2xml/", output_path);
  _wmkdir(output_directory);

  swprintf_s(src_pdf_path, MAX_FILE_PATH, L"%lsAboutFoxit.pdf", input_path);

  swprintf_s(saved_xml_path, MAX_FILE_PATH, L"%lsAboutFoxit.xml", output_directory);

  FSDK_Convert_ToXML(src_pdf_path, NULL, saved_xml_path, NULL, TRUE, &ret);
  err_ret = ret ? 0 : 1;

  FSDK_Library_Release();
  printf("Convert PDF file to XML format file %s\n",ret ? "successfully." : "failed.");
  return err_ret;
}

